/** may be used in future **/

define(["dojo/_base/declare",
        "dojo/_base/lang",
        "dojo/on",
        "dojo/dom"
       ],
   function (declare, lang, on, dom) {
    return declare(null, {
        constructor: function (options) {
            lang.mixin(this, options);
           
             
        } 

    });
 });